import { TiledHexagons } from 'tiled-hexagons'
import { useState, useEffect } from 'react'
import React, { Component } from 'react';

const openmoji = require('openmoji');
const fs = require('fs');

const numEmojis = 259;
const maxHorizontal = 19;//111
const maxVertical = Math.ceil(numEmojis / maxHorizontal);

const center = Math.round((maxHorizontal * (maxVertical / 4)) + ((maxHorizontal - 1) * (maxVertical / 4))) - Math.round((maxHorizontal) / 2);//Math.ceil(numEmojis/2)-3;
const lenHistory = Math.ceil(maxHorizontal / 2) - 1;
const lenRandom = lenHistory;
const defaultEmojis = [];
for (let index = 0; index < numEmojis; index++) {
  defaultEmojis.push(openmoji.openmojis[3073]);
}

const randomBlendIndex = [100];


var count = 1;
const Hexgrid = () => {
  const [selectedEmojis, setSelectedEmojis] = useState([]);
  var iniTileObj = newTileObject(defaultEmojis);
  const [emojiTiles, setEmojiTiles] = useState(iniTileObj)

  function getTileColour(tileIndex) {
    let colour = 'white';
    if (tileIndex <= (center - 1) && tileIndex >= (center - lenHistory)) {
      colour = '#dbf7fd';//'#d4d4d4';
    }
    if (tileIndex === center) { 
      colour = '#a4eefc';
    }
    return colour;
  }


  function newTileObject(_emojis, blends, demo) {
    let emojis = _emojis;

    //For testing, sets one hex to blend
    if (blends != null && blends.length > 1) {
      emojis[57] = blends[0];
    }

    if (selectedEmojis.length >= 1) {
      for (let i = 0; i < selectedEmojis.length; i++) {
        if (center - i > 49) {
          emojis[center - i] = selectedEmojis[selectedEmojis.length - 1 - i];
        }
      }
    }

    const tileObj = emojis.map((emoji, index) => {
      /*  
      Assign tile colour based on position in grid
      */
      let colour = getTileColour(index);

      let image = `/images/${emoji.hexcode}.svg`;
      if (demo != undefined && demo != null) {
        image = demo;
        console.log(demo);
      }

      return {
        //img: `/images/${emoji.hexcode}.svg`, 
        img: image,
        key: index,
        onClick: (e) => handleClick(index, emoji, e),
        fill: '',
        styles: {
          normal: {
            fill: colour
          },
          hover: {
            fill: '#6cc7da'
          },
          active: {
            fill: '#779df1'
          }
        }
      }
    })

    return tileObj;
  }

  const handleClick = (id, emoji, e) => {
    console.log(emoji.hexcode + ' was clicked. ID is ' + id);

    //Update emoji history:
    let newEmojis = selectedEmojis;
    newEmojis.push(emoji);
    setSelectedEmojis(newEmojis);

    var blendObj;
    let demoBlends = [];
    // fetch("http://localhost:9000/emojiblends/" + emoji.hexcode)
    // .then(res => res.json())
    // .then(data => blendObj = data)
    // .then(() => demoBlends = blendObj.relatedEmoji)
    //combineEmoji(hexcode1, hexcode2);


    fetch("http://localhost:9000/blendemojis/" + emoji.hexcode + "/" + "1F0CF")
      .then(res => res.json())
      .then(data => console.log(data))

    //var testimg;
    //var fetchurl = "http://localhost:9000/blendemojis/demo/" + emoji.hexcode + "/" + "1F0CF";
    // fetch(fetchurl, { headers: { 'Content-Type': 'blob' } })
    //   .then(response => response.body)
    //   .then(body => {
    //     const reader = body.getReader();

    //     return new ReadableStream({
    //       start(controller) {
    //         return pump();
    //         function pump() {
    //           return reader.read().then(({ done, value }) => {
    //             // When no more data needs to be consumed, close the stream
    //             if (done) {
    //               controller.close();
    //               return;
    //             }
    //             // Enqueue the next data chunk into our target stream
    //             controller.enqueue(value);
    //             return pump();
    //           });
    //         }
    //       }
    //     })
    //   })
    //   .then(stream => new Response(stream))
    //   .then(response => response.blob())
    //   .then(blob => URL.createObjectURL(blob))
    //   .then(url => testimg = url)
    //   .then(() => {
    //     var obj;
    //     var newTileObj;
    //     fetch("http://localhost:9000/randomemojis?limit=" + numEmojis.toString())
    //       .then(res => res.json())
    //       .then(data => obj = JSON.parse(data))
    //       .then(() => newTileObj = newTileObject(obj, demoBlends, testimg))
    //       .then(() => setEmojiTiles(newTileObj))
    //   })
    //   .catch(err => console.error(err));
  }

  function setupGrid() {
    var obj;
    fetch("http://localhost:9000/randomemojis?limit=" + numEmojis.toString())
      .then(res => res.json())
      .then(data => obj = JSON.parse(data))
      .then(() => iniTileObj = newTileObject(obj))
      .then(() => setEmojiTiles(iniTileObj))
  }

  window.addEventListener('DOMContentLoaded', (event) => {
    setupGrid();
  });

  return (
    <>
      <TiledHexagons
        tileSideLengths={60}
        tileGap={4}
        tileBorderRadii={9}
        maxHorizontal={maxHorizontal}
        tileTextStyles={{
          fontFamily: 'Source Sans Pro',
          fontSize: '68px',
        }}
        tiles={emojiTiles}
        onLoad={() => handleClick()}
      />
    </>
  );
}

export default Hexgrid;
