import { TiledHexagons } from 'tiled-hexagons'
import { useState, useEffect } from 'react'
import React, { Component } from 'react';

const openmoji = require('openmoji');
const fs = require('fs');

const numEmojis = 259;
const maxHorizontal = 19;//111
const maxVertical = Math.ceil(numEmojis / maxHorizontal);

const center = Math.round((maxHorizontal * (maxVertical / 4)) + ((maxHorizontal - 1) * (maxVertical / 4))) - Math.round((maxHorizontal) / 2);//Math.ceil(numEmojis/2)-3;
const lenHistory = Math.ceil(maxHorizontal / 2) - 1;
const lenRandom = lenHistory;
const defaultEmojis = [];

for (let index = 0; index < numEmojis; index++) {
  defaultEmojis.push(openmoji.openmojis[3073].hexcode);
}

const randomBlendIndex = [93, 94, 95, 96, 97, 98, 99, 100];

const Hexgrid = () => {
  const [emojiHistory, setEmojiHistory] = useState([]);
  var iniTileObj = newTileObject(defaultEmojis);
  const [emojiTiles, setEmojiTiles] = useState(iniTileObj)

  function getTileColour(tileIndex) {
    let colour = 'white';
    if (tileIndex <= (center - 1) && tileIndex >= (center - lenHistory)) {
      colour = '#dbf7fd';//'#d4d4d4';
    }
    if (tileIndex === center) {
      colour = '#a4eefc';
    }
    return colour;
  }

  function getTileImage(tileIndex, emoji, blendPartner) {
    // fetch("http://localhost:9000/blendemojis/" + emoji.hexcode + "/" + "1F0CF")
    // .then(res => res.json())
    // .then(data => console.log(data))

    let image = `/images/${emoji.hexcode}.svg`;
    return image;
  }

  function newTileObject(_emojis, selectedHexcode) {
    let hexcodes = _emojis;
    let blendedHexcodes = [];

    /*
    TODO: If selectedHexcode doesnt exist, show single emojis only
    */
    if (selectedHexcode === null || selectedHexcode === undefined) {
      /*
      Map hexcodes to tiles 
      */
      const tileObj = hexcodes.map((hexcode, index) => {
        /*  
        Assign tile colour based on position in grid
        */
        let colour = getTileColour(index);

        /*  
        Assign tile image based on position in grid
        */
        let image = `/images/${hexcode}.svg`;
        return {
          img: image,
          key: index,
          onClick: (e) => handleClick(index, hexcode, e),
          fill: '',
          styles: {
            normal: {
              fill: colour
            },
            hover: {
              fill: '#6cc7da'
            },
            active: {
              fill: '#779df1'
            }
          }
        }
      })

      return tileObj;
    }
    else {
      /*
       Insert emoji history into active tiles
       */
      if (emojiHistory.length >= 1) {
        for (let i = 0; i < emojiHistory.length; i++) {
          if (center - i > 49) {
            hexcodes[center - i] = emojiHistory[emojiHistory.length - 1 - i];
          }
        }
      }

      /*
      Get blends from backend 
      TODO: Move this to handleclick, poor placement here. not working out.
      */
      var obj;
      if (selectedHexcode !== null && selectedHexcode !== undefined) {
        fetch("http://localhost:9000/randomblendhexcodes/" + selectedHexcode + "?limit=" + randomBlendIndex.toString())
          .then(res => res.json())
          .then(data => obj = JSON.parse(data))
          .then(obj => blendedHexcodes = obj)
          .then(() => {
            /*
            Add blends into hexcodes[] 
            */
            for (let i = 0; i < hexcodes.length; i++) {
              if (randomBlendIndex.includes(i)) {
                let j = randomBlendIndex.indexOf(i);
                hexcodes[i] = blendedHexcodes[j];
              }
            }
          })
        /*
        Map hexcodes to tiles 
        */
        const tileObj = hexcodes.map((hexcode, index) => {
          /*  
          Assign tile colour based on position in grid
          */
          let colour = getTileColour(index);

          /*  
          TODO: Confirm whether hexcode is code or [code, code]
          Currently this pretends blends dont exist
          */
          if (Array.isArray(hexcode)) {
            hexcode = hexcode[0];
          }


          /*  
          Assign tile image based on position in grid
          */
          let image = `/images/${hexcode}.svg`;
          // fetch("http://localhost:9000/blendemojis/" + emoji.hexcode + "/" + "1F0CF")
          // .then(res => res.json())
          // .then(data => console.log(data))

          // if (demo != undefined && demo != null) {
          //   image = demo;
          // }

          return {
            img: image,
            key: index,
            onClick: (e) => handleClick(index, hexcode, e),
            fill: '',
            styles: {
              normal: {
                fill: colour
              },
              hover: {
                fill: '#6cc7da'
              },
              active: {
                fill: '#779df1'
              }
            }
          }
        })

        return tileObj;

      }
    }
  }

  /*
  Get hexcodes for every tile and output as array.
  TODO: Finish this
  */
  var getHexcodes = function (hexcode, callback) {
    let hexcodes = [];

    /*  
    Get random emojis
    */
    var obj;
    var newTileObj;
    fetch("http://localhost:9000/randomhexcodes?limit=" + numEmojis.toString())
      .then(res => res.json())
      .then(data => obj = JSON.parse(data))
      .then(() => console.log("here is where the next api call should occur"))
      .then(() => newTileObj = newTileObject(obj, hexcode))
      .then(() => setEmojiTiles(newTileObj))

    /*  
    Get random blends
    */

  }


  const handleClick = (id, hexcode, e) => {
    console.log(hexcode + ' was clicked. ID is ' + id);

    /*
    Update emoji history:
    TODO: Remove older entries when lenght exceeds a certain number
    */
    let newEmojis = emojiHistory;
    newEmojis.push(hexcode);
    setEmojiHistory(newEmojis);

    /*  
    Get random emojis
    TODO: Get emoji blends and pass them into newTileObject().
    Because newTileObject() was a bad place to try and make an api call.
    */
    var obj;
    var newTileObj;
    fetch("http://localhost:9000/randomhexcodes?limit=" + numEmojis.toString())
      .then(res => res.json())
      .then(data => obj = JSON.parse(data))
      .then(() => console.log("here is where the next api call should occur"))
      .then(() => newTileObj = newTileObject(obj, hexcode))
      .then(() => setEmojiTiles(newTileObj))

    //var testimg;
    //var fetchurl = "http://localhost:9000/blendemojis/demo/" + emoji.hexcode + "/" + "1F0CF";
    // fetch(fetchurl, { headers: { 'Content-Type': 'blob' } })
    //   .then(response => response.body)
    //   .then(body => {
    //     const reader = body.getReader();

    //     return new ReadableStream({
    //       start(controller) {
    //         return pump();
    //         function pump() {
    //           return reader.read().then(({ done, value }) => {
    //             // When no more data needs to be consumed, close the stream
    //             if (done) {
    //               controller.close();
    //               return;
    //             }
    //             // Enqueue the next data chunk into our target stream
    //             controller.enqueue(value);
    //             return pump();
    //           });
    //         }
    //       }
    //     })
    //   })
    //   .then(stream => new Response(stream))
    //   .then(response => response.blob())
    //   .then(blob => URL.createObjectURL(blob))
    //   .then(url => testimg = url)
    //   .then(() => {
    //     var obj;
    //     var newTileObj;
    //     fetch("http://localhost:9000/randomemojis?limit=" + numEmojis.toString())
    //       .then(res => res.json())
    //       .then(data => obj = JSON.parse(data))
    //       .then(() => newTileObj = newTileObject(obj, demoBlends, testimg))
    //       .then(() => setEmojiTiles(newTileObj))
    //   })
    //   .catch(err => console.error(err));
  }

  /*
  Display the initial emoji options.
  This is occuring twice according to the API terminal
  */
  window.addEventListener('DOMContentLoaded', (event) => {
    var obj;
    fetch("http://localhost:9000/randomhexcodes?limit=" + numEmojis.toString())
      .then(res => res.json())
      .then(data => obj = JSON.parse(data))
      .then(() => iniTileObj = newTileObject(obj))
      .then(() => setEmojiTiles(iniTileObj))
  });

  return (
    <>
      <TiledHexagons
        tileSideLengths={60}
        tileGap={4}
        tileBorderRadii={9}
        maxHorizontal={maxHorizontal}
        tileTextStyles={{
          fontFamily: 'Source Sans Pro',
          fontSize: '68px',
        }}
        tiles={emojiTiles}
        onLoad={() => handleClick()}
      />
    </>
  );
}

export default Hexgrid;
