import logo from './logo2.svg';
import './App.css';
import HexagonGrid from 'react-hexagon-grid';
import { Hexagon, TiledHexagons } from 'tiled-hexagons'
const openmoji = require('openmoji');

const om = openmoji.openmojis[99];
var len = 504//openmoji.openmojis.length;
const hexagons = Array.from(Array(len).keys())
var image = "/images/" + String(om.hexcode) + ".svg";
console.log(image);

const gridWidth = window.screen.width + 50;
const gridHeight = window.screen.height + 50;

//        <img src={image} className="App-logo" alt="logo" />
//<a
//className="App-link"
//href="https://reactjs.org"
//target="_blank"
//rel="noopener noreferrer"
//>
//Learn React
//</a>

function getTiles(){
  //var numTiles = openmoji.openmojis.length;

  var numEmojis = 69;

  const numbers = Array.from({length: numEmojis}, () => Math.floor(Math.random() * openmoji.openmojis.length));
  let emojis = new Array(numEmojis);

  for (let index = 0; index < numbers.length; index++) {
    emojis[index] = openmoji.openmojis[numbers[index]];
  }

  return (
    <TiledHexagons
      tileSideLengths={60}
      tileGap={4}
      tileBorderRadii={9}
      maxHorizontal={12}
      tileTextStyles={{
      fontFamily: 'Source Sans Pro',
      fontSize: '68px',
      fill: '#7cebff'
      }}
      tiles={emojis.map(emoji => {
        return { img: `/images/${emoji.hexcode}.svg` }
      })}
    />
  );
}
//img: logo, //replace with specific emoji images
 // onClick: () => console.log('clicked!'), //Make a proper function, pass the value to it
  //fill: '#7cebff', //get this right
 // shadow: '#64c5d6' //this too

const changeStyle = (hexagon) => {
  var hex = document.getElementsByTagName('polygon');
  hex[hexagon].style.fill = "red";
}

const getHexProps = (hexagon) => {
  var num = hexagon;
  const emoji = openmoji.openmojis[hexagon];
  var emojiimg = "/images/" + String(emoji.hexcode) + ".svg";

  return {
    style: {
      fill: "white",
      backgroundImage: {emojiimg},
      stroke: '#282c34' //'#007aff'
    },
    onClick: (num) => changeStyle(hexagon)
  };
}
const renderHexagonContent = (hexagon) => {
  const emoji = openmoji.openmojis[hexagon];
  var emojiimg = "/images/" + String(emoji.hexcode) + ".svg";

  return (
    <iframe src={logo} width="100" height="200"/>
  );
} 

function App() {
  return (    
    <div className="App">
      <header className="App-header">
        {getTiles()}
      </header>
    </div>
  );
    //
}

export default App;
