import { TiledHexagons } from 'tiled-hexagons'
import { useState, useEffect } from 'react'
import React, { Component } from 'react';

const openmoji = require('openmoji');
const fs = require('fs');

const numEmojis = 259;
const maxHorizontal = 19;//111
const maxVertical = Math.ceil(numEmojis / maxHorizontal);

const center = Math.round((maxHorizontal * (maxVertical / 4)) + ((maxHorizontal - 1) * (maxVertical / 4))) - Math.round((maxHorizontal) / 2);//Math.ceil(numEmojis/2)-3;
const lenHistory = Math.ceil(maxHorizontal / 2) - 1;
const lenRandom = lenHistory;
const defaultEmojis = [];
for (let index = 0; index < numEmojis; index++) {
  defaultEmojis.push(openmoji.openmojis[3073]);
}

var count = 1;
const Hexgrid = () => {
  const [selectedEmojis, setSelectedEmojis] = useState([]);
  var iniTileObj = newTileObject(defaultEmojis);
  const [emojiTiles, setEmojiTiles] = useState(iniTileObj)

  function newTileObject(_emojis, blends, demo) {
    let emojis = _emojis;

    //For testing, sets one hex to blend
    if (blends != null && blends.length > 1) {
      emojis[57] = blends[0];
    }

    if (selectedEmojis.length >= 1) {
      for (let i = 0; i < selectedEmojis.length; i++) {
        if (center - i > 49) {
          emojis[center - i] = selectedEmojis[selectedEmojis.length - 1 - i];
        }
      }
    }
    const tileObj = emojis.map((emoji, index) => {
      let colour = 'white';//emojiFills[index].toString();
      //console.log(selectedEmojis.length);
      if (index <= (center - 1) && index >= (center - lenHistory)) {
        colour = '#dbf7fd';//'#d4d4d4';
      }
      if (index === center) { //TODO: No magic numbers
        colour = '#a4eefc';
      }
      let image = `/images/${emoji.hexcode}.svg`;

      return {
        //img: `/images/${emoji.hexcode}.svg`, 
        img: image,
        key: index,
        onClick: (e) => handleClick(index, emoji, e),
        fill: '',
        styles: {
          normal: {
            fill: colour
          },
          hover: {
            fill: '#6cc7da'
          },
          active: {
            fill: '#779df1'
          }
        }
      }
    })

    return tileObj;
  }

  function download(emoji) {
  }

  const handleClick = (id, emoji, e) => {
    //console.log(emoji.hexcode + ' was clicked. ID is ' + id);

    //Update emoji history:
    let newEmojis = selectedEmojis;
    newEmojis.push(emoji);
    setSelectedEmojis(newEmojis);

    var blendObj;
    let demoBlends = [];
    // fetch("http://localhost:9000/emojiblends/" + emoji.hexcode)
    // .then(res => res.json())
    // .then(data => blendObj = data)
    // .then(() => demoBlends = blendObj.relatedEmoji)
    //combineEmoji(hexcode1, hexcode2);


    var testimg;
    var url = "http://localhost:9000/blendemojis/" + emoji.hexcode + "/" + "1F0CF";

    fetch(url, {headers: {'Content-Type': 'image/png'}})
      .then(response => {console.log(response.data)})
      // .then(imageBlob => {
      //   // Then create a local URL for that image and print it 
      //   //const imageObjectURL = URL.createObjectURL(imageBlob);
      //   console.log(imageBlob);
      //   //testimg = imageObjectURL;
      // }); 

    // if(testimg === undefined){
    //   console.log('testimg is undefined');
    // }
    // else{
    //   console.log(testimg);
    // }

    var obj;
    var newTileObj;
    fetch("http://localhost:9000/randomemojis?limit=" + numEmojis.toString())
      .then(res => res.json())
      .then(data => obj = JSON.parse(data))
      .then(() => newTileObj = newTileObject(obj, demoBlends))
      .then(() => setEmojiTiles(newTileObj))
  }

  function setupGrid() {
    var obj;
    // var iniTileObj = newTileObject(defaultEmojis);

    fetch("http://localhost:9000/randomemojis?limit=" + numEmojis.toString())
      .then(res => res.json())
      .then(data => obj = JSON.parse(data))
      .then(() => iniTileObj = newTileObject(obj))
      .then(() => setEmojiTiles(iniTileObj))
  }

  window.addEventListener('DOMContentLoaded', (event) => {
    setupGrid();
  });

  return (
    <>
      <TiledHexagons
        tileSideLengths={60}
        tileGap={4}
        tileBorderRadii={9}
        maxHorizontal={maxHorizontal}
        tileTextStyles={{
          fontFamily: 'Source Sans Pro',
          fontSize: '68px',
        }}
        tiles={emojiTiles}
        onLoad={() => handleClick()}
      />
    </>
  );
}

export default Hexgrid;
