import { Hexagon, TiledHexagons } from 'tiled-hexagons'
import { useState, useEffect } from 'react'
import React, { Component } from 'react';

const openmoji = require('openmoji');

const defaultEmojis = [];
for (let index = 0; index < 113; index++) {
  defaultEmojis.push(openmoji.openmojis[3073]);
}
const defaultFills = [];
for (let index = 0; index < 113; index++) {
  if (index === 56) {
    defaultFills.push('#a4eefc');
  } else {
    defaultFills.push('white');
  }
}

//3073

const Hexgrid = () => {
  const [selectedEmojis, setSelectedEmojis] = useState([]);
  var iniTileObj = newTileObject(defaultEmojis);
  const [emojiTiles, setEmojiTiles] = useState(iniTileObj)
  const [emojiFills, setEmojiFills] = useState(defaultFills)

  //setupGrid();
  // function generateEmojis() {

  //   var numEmojis = 113; //id 56 is the center

  //   let numbers = [];
  //   let emojis = [];

  //   while (numbers.length < numEmojis) {
  //     var r = Math.floor(Math.random() * (openmoji.openmojis.length - 1));
  //     if (numbers.indexOf(r) === -1) numbers.push(r);
  //   }

  //   for (let index = 0; index < numbers.length; index++) {
  //     emojis.push(openmoji.openmojis[numbers[index]]);
  //   }
  //   console.log("USED GENERATE EMOJIS: ");
  //   console.log(emojis);

  //   return emojis;   // The function returns the product of p1 and p2
  // }

  function newTileObject(_emojis){
    let emojis = _emojis;
    //let emojiFills = [];
    for (let index = 0; index < emojis.length; index++) {
      if (index === 56) {
        emojiFills.push('#a4eefc');
      } else {
        emojiFills.push('white');
      }
    }

    if (selectedEmojis.length >= 1) {
      for (let i = 0; i < selectedEmojis.length; i++) {
        if(56-i > 49){
          emojis[56-i] = selectedEmojis[selectedEmojis.length-1-i];
          emojiFills[56-i] = '#a4eefc';
        }
      }
    }
    const tileObj = emojis.map((emoji, index) => {
      let colour = emojiFills[index].toString();
        return { 
          img: `/images/${emoji.hexcode}.svg`, 
          key: index,
          onClick: (e) => handleClick(index, emoji, e),
          fill: '',
          styles: {
            normal: {
                fill: colour
            },
            hover: {
              fill: '#6cc7da'
            },
            active: {
              fill: '#779df1'
            }
        }
        }
      })

      return tileObj;
  }

  const handleClick = (id, emoji, e) => {
    console.log(emoji.hexcode + ' was clicked. ID is ' + id);

    // fetch("http://localhost:9000/emojiblends/" + openmoji.openmojis[99].hexcode)
    // .then(res => res.json())
    // .then(data => console.log(data.hexcode))

    //Update emoji history:
    let newEmojis = selectedEmojis;
    newEmojis.push(emoji);
    setSelectedEmojis(newEmojis);

    var obj;
    var newTileObj;
    fetch("http://localhost:9000/randomemojis?limit=113")
    .then(res => res.json())
    .then(data => obj = JSON.parse(data))
    .then(() => newTileObj = newTileObject(obj))
    .then(() => setEmojiTiles(newTileObj))
  }

  function setupGrid(){
    var obj;
    // var iniTileObj = newTileObject(defaultEmojis);

    fetch("http://localhost:9000/randomemojis?limit=3")
      .then(res => res.json())
      .then(data => obj = JSON.parse(data))
      .then(() => iniTileObj = newTileObject(obj))
      .then(() => setEmojiTiles(iniTileObj))
      .then(() => console.log("INITIAL WAVE."))
  }

  // fetch("http://localhost:9000/randomemojis/")
  // .then(res => res.json())
  // .then(data => obj = JSON.parse(data))
  // .then(() => iniTileObj = newTileObject(obj))
  // .then(() => setEmojiTiles(iniTileObj))
  // .then(() => console.log("INITIAL WAVE."))

  //const iniTileObj = newTileObject(generateEmojis());

    return ( 
        <>
            <TiledHexagons
            tileSideLengths={60}
            tileGap={4}
            tileBorderRadii={9}
            maxHorizontal={13}
            tileTextStyles={{
            fontFamily: 'Source Sans Pro',
            fontSize: '68px',
            }}
            tiles={emojiTiles}
            />
    </>
    );
}

export default Hexgrid;
