import { Hexagon, TiledHexagons } from 'tiled-hexagons'
import { useState } from 'react'
import { Component } from 'react'

const openmoji = require('openmoji');

const Hexgrid = () => {


    const selectedEmojis = [];
    var numEmojis = 69; //69


    const handleClick = (id, emoji, e) => {
        console.log(emoji.hexcode + ' was clicked. ID is ' + id);
        //selectedEmojis.push(emoji);
        setEmojiTiles(
            emojis.map((emoji, index) => {
                return { 
                  img: `/images/${emoji.hexcode}.svg`, 
                  key: index,
                  onClick: (e) => handleClick(index, emoji, e),
                  fill: '#70cbff'
                }
              })
        )
    }


    let numbers = [];
    while(selectedEmojis.length + numbers.length < numEmojis){
        var r = Math.floor(Math.random() * (openmoji.openmojis.length - 1));
        if(numbers.indexOf(r) === -1) numbers.push(r);
    }
    let emojis = [];

    for (let index = 0; index < numbers.length; index++) {
        emojis.push(openmoji.openmojis[numbers[index]]);
    }    


    emojis = selectedEmojis.concat(emojis); 
    const [emojiTiles, setEmojiTiles] = useState(
        emojis.map((emoji, index) => {
            return { 
              img: `/images/${emoji.hexcode}.svg`, 
              key: index,
              onClick: (e) => handleClick(index, emoji, e),
              fill: 'white'
            }
          })
    )
    //emojis[0].fill = "red";

    return (  
        <TiledHexagons
      tileSideLengths={60}
      tileGap={4}
      tileBorderRadii={9}
      maxHorizontal={12}
      tileTextStyles={{
      fontFamily: 'Source Sans Pro',
      fontSize: '68px',
      }}
      tiles={emojiTiles}
      tileStyles={{
        normal: {
            fill: 'red'
        },
        hover: {
            fill: 'blue'
        }
    }}

    />
    );
}

export default Hexgrid;
