import './App.css';
import { Hexagon, TiledHexagons } from 'tiled-hexagons'
const openmoji = require('openmoji');


function getTiles(){
  //var numTiles = openmoji.openmojis.length;

  var numEmojis = 69;

  const numbers = Array.from({length: numEmojis}, () => Math.floor(Math.random() * openmoji.openmojis.length));
  let emojis = new Array(numEmojis);

  for (let index = 0; index < numbers.length; index++) {
    emojis[index] = openmoji.openmojis[numbers[index]];
  }

  return (
    <TiledHexagons
      tileSideLengths={60}
      tileGap={4}
      tileBorderRadii={9}
      maxHorizontal={12}
      tileTextStyles={{
      fontFamily: 'Source Sans Pro',
      fontSize: '68px',
      fill: '#7cebff'
      }}
      tiles={emojis.map(emoji => {
        return { 
          img: `/images/${emoji.hexcode}.svg`, 
          onClick: () => clickEmoji(emoji)
        }
      })}
    />
  );
}

const clickEmoji = (emoji) => {
  console.log(emoji.hexcode + ' was clicked.');
}

function App() {
  return (    
    <div className="App">
      <header className="App-header">
        {getTiles()}
      </header>
    </div>
  );
    //
}

export default App;
