import logo from './logo2.svg';
import './App.css';
import HexagonGrid from 'react-hexagon-grid';
const openmoji = require('openmoji');

const om = openmoji.openmojis[99];
var len = 504//openmoji.openmojis.length;
const hexagons = Array.from(Array(len).keys())
var image = "/images/" + String(om.hexcode) + ".svg";
console.log(image);

const gridWidth = window.screen.width + 50;
const gridHeight = window.screen.height + 50;

//        <img src={image} className="App-logo" alt="logo" />
//<a
//className="App-link"
//href="https://reactjs.org"
//target="_blank"
//rel="noopener noreferrer"
//>
//Learn React
//</a>

const changeStyle = (hexagon) => {
  var hex = document.getElementsByTagName('polygon');
  hex[hexagon].style.fill = "red";
  console.log(hex.length);
}
function showImages() { 
  hexagons.forEach(showImage);
}
function showImage(img) { 
  var hex = document.getElementsByTagName('polygon');
  var imgElement = document.createElement('img');
  imgElement.src = logo;

  console.log(hex);
  if (hex.length > 0){
    hex[hexagon].insertAdjacentElement('beforebegin', imgElement);
  }
}


const getHexProps = (hexagon) => {
  var num = hexagon;
  return {
    style: {
      fill: 'white',
      stroke: '#282c34' //'#007aff'
    },
    onClick: (num) => changeStyle(hexagon)
  };
}
const renderHexagonContent = (hexagon) => {
  const emoji = openmoji.openmojis[hexagon];
  var emojiimg = "/images/" + String(emoji.hexcode) + ".svg";
  return (
    <img
    src={logo}
    alt="triangle with all three sides equal"
    height={100}
    width={100} />

  );
}
function App() {

  return (
    <div className="App">
      <header className="App-header">
        <HexagonGrid className="App-grid" gridWidth={gridWidth} gridHeight={gridHeight} hexagons={hexagons}
         hexProps={getHexProps}
         renderHexagonContent={renderHexagonContent}
        />
        <script>showImages();</script>
      </header>
    </div>
  );
}

export default App;
