import { Hexagon, TiledHexagons } from 'tiled-hexagons'
import { useState } from 'react'
import React from 'react';

const openmoji = require('openmoji');

class Grid extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
          colour: 'purple'
        }
      }

      selectHex() {
          if (this.state.colour === 'red') {
            this.setState({ colour: 'blue' })
        } else {
            this.setState({ colour: 'red' })
        }
      }

    render() {
        let { colour } = this.state



        return (
            <Hexagon
              sideLength={80}
              fill={colour.toString()}
              onClick={this.selectHex.bind(this)}
            />  )  }
  }


const Hexgrid = () => {
  function generateEmojis(numSelected) {

    var numEmojis = 113; //id 56 is the center

    let numbers = [];
    let emojis = [];

    while(numSelected + numbers.length < numEmojis){
      var r = Math.floor(Math.random() * (openmoji.openmojis.length - 1));
      if(numbers.indexOf(r) === -1) numbers.push(r);
  }

  for (let index = 0; index < numbers.length; index++) {
      emojis.push(openmoji.openmojis[numbers[index]]);
  }    

    return emojis;   // The function returns the product of p1 and p2
  }

  function newTileObject(){
    const [selectedEmojis, setSelectedEmojis] = useState([]);

let emojis = generateEmojis(selectedEmojis.length);

    let emojiFills = [];
    for (let index = 0; index < emojis.length; index++) {
      if (index === 56) {
        emojiFills.push('#a4eefc');
      } else {
        emojiFills.push('white');
      }
    }   

    const tileObj = emojis.map((emoji, index) => {
      let colour = emojiFills[index].toString();
        return { 
          img: `/images/${emoji.hexcode}.svg`, 
          key: index,
          onClick: (e) => handleClick(index, emoji, e),
          fill: '',
          styles: {
            normal: {
                fill: colour
            },
            hover: {
              fill: '#6cc7da'
            },
            active: {
              fill: '#779df1'
            }
        }
        }
      })

      return tileObj;
  }

  const handleClick = (id, emoji, e) => {
    let newEmojis = selectedEmojis;
    newEmojis.push(emoji);
    setSelectedEmojis(newEmojis);
    let emojis = generateEmojis(0);
    console.log(emoji.hexcode + ' was clicked. ID is ' + id);
    if (selectedEmojis.length >= 1) {
      for (let i = 0; i < selectedEmojis.length; i++) {
        if(56-i > 49){
          emojis[56-i] = selectedEmojis[selectedEmojis.length-1-i];
          emojiFills[56-i] = '#a4eefc';
        }
      }

      const tileObj = newTileObject()
      setEmojiTiles(tileObj);
    }
    console.log(emojiTiles);

  }


  const iniTileObj = newTileObject();
  const [emojiTiles, setEmojiTiles] = useState(iniTileObj)
    return ( 
        <>
            <Grid /> 
            <TiledHexagons
            tileSideLengths={60}
            tileGap={4}
            tileBorderRadii={9}
            maxHorizontal={13}
            tileTextStyles={{
            fontFamily: 'Source Sans Pro',
            fontSize: '68px',
            }}
            tiles={emojiTiles}
            />
    </>
    );
}

export default Hexgrid;
