import './App.css';
import { Hexagon, TiledHexagons } from 'tiled-hexagons'
const openmoji = require('openmoji');
import React from 'react';

const selectedEmojis = new Array();

function getTiles(){
  var numEmojis = 69 - selectedEmojis.length;

  const numbers = Array.from({length: numEmojis}, () => Math.floor(Math.random() * openmoji.openmojis.length));
  let emojis = new Array(numEmojis);

  for (let index = 0; index < numbers.length; index++) {
    emojis[index] = openmoji.openmojis[numbers[index]];
  }

  emojis = selectedEmojis.concat(emojis); 

  const grid = (
    <TiledHexagons
        tileSideLengths={60}
        tileGap={4}
        tileBorderRadii={9}
        maxHorizontal={12}
        tileTextStyles={{
        fontFamily: 'Source Sans Pro',
        fontSize: '68px',
        fill: '#7cebff'
        }}
        tiles={emojis.map(emoji => {
          return { 
            img: `/images/${emoji.hexcode}.svg`, 
            onClick: () => clickEmoji(emoji)
          }
        })}
    />
  )
  ReactDOM.render(grid, document.getElementById('root'));
}

const clickEmoji = (emoji) => {
  console.log(emoji.hexcode + ' was clicked.');
  selectedEmojis.push(emoji);
  App();
}

function App() {
  return (    
    <div className="App">
      <header className="App-header">
      </header>
      <div id="root">
      </div>
      {getTiles()}
    </div>
  );
    //
}

export default App;
